mapper.select:
select count(*) from article



            and author = #{article.author.account}


            and title = #{article.title}


            and date_time >= #{article.datetime}


            and tag = #{article.tag}


            and article.catalog = #{article.catalog.id}

select article.id, article.title, article.content, article.date_time, article.tag, user.account as user_account, user.nickname as user_nickname, user.brief as user_brief, photo.id as cover_id, photo.description as cover_description, photo.latitude as cover_latitude, photo.longitude as cover_longitude, photo.md_five as cover_md5, photo.path as cover_path, photo.upload_date as cover_date, article_catalog.id as catalog_id, article_catalog.catalog as catalog_catalog from article left join user on article.author = user.account left join photo on article.cover = photo.id left join article_catalog on article.catalog = article_catalog.id
and author = #{article.author.account}
and title = #{article.title}
and date_time >= #{article.datetime}
and tag = #{article.tag},
and article.catalog = #{article.catalog.id}
limit #{pagination.start}, #{pagination.limit}
